    val computedLineHeight = computeLineHeight(style)
    val lineHeightPx = with(density) { computedLineHeight.toPx() }
    val fadeWidthPx = fontSizePx * 12f

    Box(modifier = Modifier.fillMaxWidth()) {
      Text(
          text = text,
          style = style,
