private fun computeLineHeight(style: TextStyle): TextUnit {
  return if (style.lineHeight.isUnspecified) {
    style.fontSize * 1.2f
  } else {
    style.lineHeight
  }
}

private fun fadeModifier(
    isExpanded: Boolean,
    showToggle: Boolean,
    fadeWidthPx: Float,
    lineHeightPx: Float
): Modifier {
  if (isExpanded || !showToggle) return Modifier

  return Modifier.graphicsLayer(compositingStrategy = CompositingStrategy.Offscreen)
      .drawWithContent {
        drawContent()
        val endX = size.width
        val startX = (endX - fadeWidthPx).coerceAtLeast(0f)
        val topY = (size.height - lineHeightPx).coerceAtLeast(0f)
        val width = (endX - startX).coerceAtLeast(0f)
        if (width > 0f && lineHeightPx > 0f) {
          drawRect(
              brush =
                  Brush.horizontalGradient(
                      colors = listOf(Color.Black, Color.Transparent),
                      startX = startX,
                      endX = endX),
              topLeft = Offset(startX, topY),
              size = Size(width, lineHeightPx),
              blendMode = BlendMode.DstIn)
        }
      }
}

@Composable
private fun ToggleSection(
    showToggle: Boolean,
    isExpanded: Boolean,
    onToggleExpand: () -> Unit,
    toggleLabels: ToggleLabels,
    toggleTypography: TextStyle,
    toggleTestTag: String?
) {
  if (showToggle) {
    Row(modifier = Modifier.fillMaxWidth(), horizontalArrangement = Arrangement.End) {
      TextButton(
          onClick = onToggleExpand,
          contentPadding = PaddingValues(0.dp),
          modifier = if (toggleTestTag != null) Modifier.testTag(toggleTestTag) else Modifier) {
            Text(
                text = if (isExpanded) toggleLabels.collapse else toggleLabels.expand,
                style = toggleTypography,
                color = MaterialTheme.colorScheme.primary)
